import { Component } from '@angular/core';
import { Empleado } from './empeado.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo = 'Lista Empleados';
  empleados:Empleado[]=[
    new Empleado("Juan", "Diaz", "Scrum Master", 5000),
    new Empleado("Pablo", "Gallegos", "Tech Leader", 4000),
    new Empleado("Jennifer", "Lopez", "Devops", 3500),
    new Empleado("Mario", "Zuñiga", "Desarrollador FullStack", 3000),
    new Empleado("Natalia", "Ramirez", "Analista QA", 2000),
  ]
  agregarEMpleado(){
    let miEmpleado= new Empleado(this.cuadroNombre, this.cuadroApellido, this.cuadroCargo, this.cuadroSueldo);
    this.empleados.push(miEmpleado);
  }

  cuadroNombre: string="";
  cuadroApellido:string="";
  cuadroCargo: string="";
  cuadroSueldo:number=0;



}
<div class="container">
  <div class="col" style="text-align: center;"><h1>{{titulo}}</h1></div>
  <br><br>
  <form class="form-group row">
    <div class="form-group col-md-3">
      <label for="nombre">Nombre:</label>
      <input type="text" name="nombre" id="nombre" placeholder="Nombre" class="form-group" [(ngModel)]="cuadroNombre">
    </div>
    <br>
    <div class="form-group col-md-3">
      <label for="apellido">Apellido:</label>
      <input type="text" name="apellido" id="apellido" placeholder="Apellido" class="form-group" [(ngModel)]="cuadroApellido">
    </div>
    <div class="form-group col-md-3">
      <label for="cargo">Cargo:</label>
      <input type="text" name="cargo" id="cargo" placeholder="Cargo" class="form-group" [(ngModel)]="cuadroCargo">
    </div>
    <div class="form-group col-md-3">
      <label for="sueldo">Sueldo: </label>
      <input type="number" name="sueldo" id="sueldo" placeholder="Sueldo" class="form-group" [(ngModel)]="cuadroSueldo">
    </div>
    <div class="col">
      <button type="submit" class="btn btn-primary" (click)="agregarEmpleado()"></button>
    </div>

  </form>
</div>



<div class="container">
  <div class="row">
    <div class="col">
      <div *ngFor="let empleado of empleados; let i= index">
        {{i+1}}: {{empleado.nombre}} {{empleado.apellido}} {{empleado.cargo}} {{empleado.sueldo}}
      </div>
    </div>
  </div>
</div>
